import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import { withFormik, Form, Field } from "formik";
import * as Yup from "yup";
import axios from "axios";
import styled from "styled-components";
import "../App.css";
import SignInForm from "./SignInForm";

const FormDiv = styled.div`
  width: 75%;
  margin: auto;
  height: 60vh;

  hr {
    border: solid 1px #264653;
  }

  p {
    color: #264653;
  }

  .Form-error {
    width: 20%;
    margin: auto;
    font-size: 0.7em;
    color: red;

    @media (max-width: 800px) {
      width: 35%;
    }
  }
`;

const CreateAccountForm = ({ values, errors, touched, status }) => {
  document.title = "Create Account | SHNT";

  const [createAccount, setCreateAccount] = useState([]);

  return (
    <FormDiv>
      <Form>
        <p>E-mail:</p>
        <label htmlFor="email">
          <Field id="email" type="text" name="email" className="form-field" />
          {touched.email && errors.email && (
            <p className="Form-error">{errors.email}</p>
          )}
        </label>
        <p>Password:</p>
        <label htmlFor="password">
          <Field
            id="password"
            type="password"
            name="password"
            className="form-field"
          />
          {touched.email && errors.password && (
            <p className="Form-error">{errors.password}</p>
          )}
        </label>
        <p>Verify Password:</p>
        <label htmlFor="verifypassword">
          <Field
            id="verifypassword"
            type="password"
            name="verifypassword"
            className="form-field" 
          />  
        </label>
        <br></br>
        <button type="submit">Log In</button>
        </Form>
        <Link to="/">
          <button className="back-to-signin">Go Back to Sign In</button>
      </Link>
    </FormDiv>
  );
};

const FormikCreateAccountForm = withFormik({
  mapPropsToValues(props) {
    return {
      email: props.email || "",
      password: props.password || "",
      verifypassword: props.verifypassword || "",
    };
  },

  validationSchema: Yup.object().shape({
    email: Yup.string().required("E-mail is required!"),
    password: Yup.string().required("Password is required!"),
    verifypassword: Yup.string().oneOf[Yup.ref("")]
  }),

  handleSubmit(values, { setStatus, resetForm }) {
    axios
      .post("https://reqres.in/api/users/", values)
      .then(res => {
        console.log("success", res);

        resetForm();
      })
      .catch(err => console.log(err.response));
  },
})(CreateAccountForm);

export default FormikCreateAccountForm;
